mapper.select:
SELECT * FROM
	(
	SELECT ROWNUM AS rn, tbl.* FROM
		(
		SELECT * FROM claim_reply
		WHERE bno = #{bno}
		ORDER BY rno DESC
		) tbl
	)

WHERE rn > (#{paging.pageNum}-1) * #{paging.cpp}
AND rn <= #{paging.pageNum} * #{paging.cpp}

SELECT COUNT (*)
FROM claim_reply
WHERE bno = #{bno}

mapper.update:
UPDATE claim_reply
SET content = #{content}, reg_date = current_date
WHERE rno = #{rno}
