mapper.select:
SELECT * FROM   
	(
	SELECT ROWNUM AS rn, tbl.* FROM
		(
		SELECT * FROM board_notice

		ORDER BY reg_date DESC
		) tbl
	)

WHERE rn > (#{pageNum}-1) * #{cpp}
AND rn <= #{pageNum} * #{cpp}

mapper.update:
UPDATE board_notice
SET count = count + 1
WHERE bno = #{bno}

UPDATE board_notice
SET title=#{title}, content=#{content}, reg_date=sysdate
WHERE bno=#{bno}
