mapper.select:

  	SELECT * FROM	
  		(
  		SELECT ROWNUM AS rn, tbl.* FROM
	  		(
	  		SELECT * FROM doctor_reply
	  		WHERE doctor_no = #{doctorNo}
	  		ORDER BY rno DESC
	  		) tbl
	  	)	

  	WHERE rn > (#{paging.pageNum}-1) * #{paging.cpp}
  	AND rn <= #{paging.pageNum} * #{paging.cpp}

SELECT COUNT(*)
FROM doctor_reply
WHERE doctor_no = #{doctorNo}

mapper.update:
UPDATE doctor_reply
SET content = #{content}  
WHERE doctor_no = #{doctorNo}
AND rno = #{rno}

mapper.delete:
DELETE FROM doctor_reply
WHERE doctor_no = #{doctorNo}
AND rno = #{rno}
